import React from 'react';
import './readMyBlog.css';
import { useParams } from 'react-router-dom';

function ReadMyBlog({ blogData }) {
    const param=useParams();
    const id=param.id;
    const data=blogData?.find(data=>data.id==id);
  return (
    <div className="blog-card">
      <h2 className="blog-title">{data.title}</h2>
      <p className="blog-content">{data.content}</p>
      <span className="blog-author">â€” {data.author}</span>

      <div className="blog-buttons">
        <button className="btn update-btn" onClick={onUpdate}>Update</button>
        <button className="btn delete-btn" onClick={onDelete}>Delete</button>
      </div>
    </div>
  );
}

export default ReadMyBlog;
